        return new Source("{% extends 'base.html.twig' %}

{% block title %}Hello ResetPasswordController!{% endblock %}

{% block body %}
    {% for message in app.flashes('success') %}
        <div class=\"alert alert-success\" style=\"background: #86d9ab !important;\">
            <div class=\"container\">
                <button type=\"button\" class=\"close\" data-dismiss=\"alert\" aria-label=\"Close\">
                    <i class=\"fa fa-times\"></i>
                </button>
                <span><i class=\"fa fa-bell\"></i> {{ message|raw }}</span>
            </div>
        </div>
    {% endfor %}
<div class=\"container mt-4\">
    {{ form_start(form) }}
    {{ form_widget(form) }}
    <button class=\"btn btn-primary\">Réinitialiser mon mot de passe</button>
    {{ form_end(form) }}
</div>
{% endblock %}
", "reset_password/index.html.twig", "/Users/tomaccess/Documents/Sites/reciplink/templates/reset_password/index.html.twig");
