    public function getSourceContext()
    {
        return new Source("{% extends 'admin/base.html.twig' %}

{% block title %}Administration des recettes{% endblock %}

{% block body %}

    <div class=\"container-fluid\">
        <h1 class=\"my-5\">
            Gestion des tags
            <a href=\"{{ path('admin_tags_new') }}\" class=\"btn btn-success\">Nouveau tag</a>
        </h1>

        <table class=\"table table-hover\">
            <thead>
            <tr>
                <th>id</th>
                <th>Nom</th>
                <th class=\"text-center\">Recettes associées</th>
                <th>Actions</th>
            </tr>
            </thead>
            <tbody>
            {% for tag in tags %}
                <tr>
                    <td>{{ tag.id }}</td>
                    <td>{{ tag.name }}</td>
                    <td class=\"text-center\"><span class=\"badge badge-primary rounded\">{{ tag.recipe|length }}</span> </td>
                    <td>
                        <a href=\"{{path('admin_tags_edit', {'id': tag.id})}}\" class=\"btn btn-primary\"><i class=\"fa fa-edit\"></i></a>
                        <a onclick=\"return confirm('êtes vous sûr de supprimer le tag ?')\" href=\"{{path('admin_tags_delete', {'id': tag.id})}}\" class=\"btn btn-danger\"><i class=\"fa fa-trash\"></i></a>
                    </td>
                </tr>
            {% endfor %}
            </tbody>
        </table>
    </div>
{% endblock %}
", "admin/tags/index.html.twig", "/home/cortez/Sites/reciplink/templates/admin/tags/index.html.twig");
